    private void txt_CorreoKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txt_CorreoKeyTyped
        String c=evt.paramString();
        String correo = null;
        while (correo == null) {
            correo = txt_Correo.getText();
            if (correo.contains("@")) {
                //validar que no tiene más de 1 "@"
                int contA = 0;
                for (int i = 0; i < correo.length(); i++) {
                    if (correo.charAt(i) == '@') {
                        contA++;
                    }
                }
                if (contA == 1) {
                    // continuación de las validaciones
                    String[] lista = correo.split("@");
                    //validar que el dominio no tiene más de un punto
                    int contP = 0;
                    for (int i = 0; i < lista[1].length(); i++) {
                        if (lista[1].charAt(i) == '.') {
                            contP++;
                        }
                    }
                    if (contP == 1) {
                        //continuacion de las validaciones
                        String[] dominio = lista[1].split("\\.");
                        //validar el tipo
                        if (dominio[1].equals("es") || dominio[1].equals("com") || dominio[1].equals("org")) {
                        }else {
                            JOptionPane.showMessageDialog(null,"Tipo del dominio erróneo");
                            correo = null;
                        }
                    } else {
                        JOptionPane.showMessageDialog(null,"Formato del dominio incorrecto");
                        correo = null;
                    }
                } else {
                    JOptionPane.showMessageDialog(null,"Formato del correo incorrecto");
                    correo = null;
                }
            }else {
                JOptionPane.showMessageDialog(null,"Formato del correo incorrecto");
                correo = null;
            }
        }
      
    }//GEN-LAST:event_txt_CorreoKeyTyped

