    private void txt_fechaKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txt_fechaKeyTyped
         int mes = 0;
        while (mes == 0) {
            try {
                mes = Llamar.lerInt("Fecha de nacimiento\nMes:");
                if (mes < 1 || mes > 12) {
                    System.out.println("Mes inválido");
                    mes = 0;
                }
            } catch (NumberFormatException ex) {
                System.out.println("El formato debe ser numérico");
                mes = 0;
            }
        }
        int anho = 0;
        while (anho == 0) {
            try {
                anho = Llamar.lerInt("Fecha de nacimiento\nAño:");
                if (anho < 1910 || anho > 2050) {
                    System.out.println("Año fuera del límite permitido");
                    anho = 0;
                }
            } catch (NumberFormatException ex) {
                System.out.println("El formato debe de ser numérico");
                anho = 0;
            }
        }
        int dia = 0;
        while (dia == 0) {
            try {
                dia = Llamar.lerInt("Fecha de nacimiento\nDía:");
                switch (mes) {
                    case 1:
                    case 3:
                    case 5:
                    case 7:
                    case 8:
                    case 10:
                    case 12:
                        if (dia < 1 || dia > 31) {
                            System.out.println("Día incorrecto");
                            dia = 0;
                        }
                        break;
                    case 4:
                    case 6:
                    case 9:
                    case 11:
                        if (dia < 1 || dia > 30) {
                            System.out.println("Día incorrecto");
                            dia = 0;
                        }
                        break;
                    case 2:
                        if ((anho % 4)==0 && (anho % 100)!=0) {
                            if (dia < 1 || dia > 29) {
                                System.out.println("Día incorrecto");
                                dia = 0;
                            }
                        } else {
                            if ((anho % 100)==0 && (anho % 400)==0) {
                                if (dia < 1 || dia > 29) {
                                    System.out.println("Día incorrecto");
                                    dia = 0;
                                }
                            } else {
                                if (dia < 1 || dia > 28) {
                                    System.out.println("Día incorrecto");
                                    dia = 0;
                                }
                            }
                        }
                        break;
                    default:
                        break;
                }
            } catch (NumberFormatException ex) {
                System.out.println("El formato debe de ser numérico");
            }
        }

        String fecha = dia+"/"+mes+"/"+anho;
    }//GEN-LAST:event_txt_fechaKeyTyped

